{"changed":true,"filter":false,"title":"exam.rb","tooltip":"/lib/my_gem/exam.rb","value":"require_relative \"list\"\nrequire_relative \"test\"\n\nclass Exam\n\tattr_accessor :listp, :correct, :fail, :nota, :answer\n\tdef initialize(p)\n\t\t@listp=List.new\n\t\tfor i in(0...p.size)\n\t\t\t@listp.pushhead(p[i])\n\t\tend\n\t\t@correct=0\n\t\t@fail=0\n\t\t@nota=0\n\t\t@answer=nil\n\tend\n\tdef checkquestion\n\t\t#imprime pregunta y pide respuesta por pantalla.\n\t\t@listp.tail.value.show()\n\t\tif @answer==@listp.tail.value.t\n\t\t\t@correct+=1\n\t\telse\n\t\t\t@fail+=1\n\t\tend\n\t\t@listp.poptail\n\tend\nend\n\nclass Interfaz\n\tattr_accessor :exam\n\tdef initialize(e)\n\t\t@exam=e\n\tend\n\tdef test\n\t\tfor i in(0...@exam.listp.size)\n\t\t\t@exam.checkquestion\n\t\tend\n\t\t@exam.nota=@exam.correct*10/(@exam.correct+@exam.fail)\n\tend\nend\n","undoManager":{"mark":36,"position":50,"stack":[[{"group":"doc","deltas":[{"start":{"row":16,"column":50},"end":{"row":17,"column":0},"action":"insert","lines":["",""]},{"start":{"row":17,"column":0},"end":{"row":17,"column":2},"action":"insert","lines":["\t\t"]}]}],[{"group":"doc","deltas":[{"start":{"row":17,"column":2},"end":{"row":17,"column":3},"action":"insert","lines":["t"]}]}],[{"group":"doc","deltas":[{"start":{"row":17,"column":3},"end":{"row":17,"column":4},"action":"insert","lines":["e"]}]}],[{"group":"doc","deltas":[{"start":{"row":17,"column":3},"end":{"row":17,"column":4},"action":"remove","lines":["e"]}]}],[{"group":"doc","deltas":[{"start":{"row":17,"column":2},"end":{"row":17,"column":3},"action":"remove","lines":["t"]}]}],[{"group":"doc","deltas":[{"start":{"row":17,"column":2},"end":{"row":17,"column":3},"action":"insert","lines":["@"]}]}],[{"group":"doc","deltas":[{"start":{"row":17,"column":3},"end":{"row":17,"column":4},"action":"insert","lines":["l"]}]}],[{"group":"doc","deltas":[{"start":{"row":17,"column":4},"end":{"row":17,"column":5},"action":"insert","lines":["i"]}]}],[{"group":"doc","deltas":[{"sta